import React from 'react'
import moment from 'moment';
import {Chart} from 'primereact/chart';
import Media from 'react-media';

function WeatherChart( {weatherState} ) {

    let daily = null;
    let data = null;
    // console.log('chart props', weather, isLoaded);
    if(weatherState.isLoaded && !weatherState.isLoading) {
        daily = weatherState.weather.daily;
        // console.log('daily', daily);
        data = {
        labels: daily.data.map((daily) => moment.unix(daily.time).format('ddd DD/MM')),
        datasets: [
            {
                label: 'Temperature High',
                data: daily.data.map((daily) => daily.apparentTemperatureHigh),
                fill: false,
                backgroundColor: '#f7a800',
                borderColor: '#f7a800'  
            },
            {
                label: 'Temperature Low',
                data: daily.data.map((daily) => daily.apparentTemperatureLow),
                fill: false,
                backgroundColor: '#42A5F5',
                borderColor: '#42A5F5'
            }
        ]   
    };
    // console.log('data', data);
  }
  return (
    <Media query="(max-width: 500px)">
          {matches =>
            matches ? (              
            <div>      
              {(data !== null) && <Chart type="line" data={data} width="400px" /> }
            </div>
            ) : (
            <div>      
              {(data !== null) && <Chart type="line" data={data} width="500px" /> }
            </div>
            )
          }
        </Media>
  )
}

export default WeatherChart;
