import React, { Component } from 'react';
// import { Map, TileLayer, Marker, Popup } from 'react-leaflet';
import ReactMapGL from 'react-map-gl';
// import { Card } from 'primereact/card';
// import 'leaflet/dist/leaflet.css';
//pk.eyJ1IjoicmVkdmVsb2NpdHkiLCJhIjoiY2p1bDVzMmwzMXg5YjRkb2FjNXIwOWFnbCJ9.99QKd-qk4VJI4I8bj8rUWg

export default class MapCard extends Component {

  state = {
    viewport: {
      latitude: 12.9791198,
      longitude: 77.5912997,
      zoom: 10
    }
  };

    setPosition = (lat, lon) => {
        this.setState({viewport: {latitude:lat, longitude:lon}});
      }
    
 render(){ 
    // let position = [this.state.viewport.latitude, this.state.viewport.longitude];
    // if(this.props.weatherState.isLoaded) {
    //   this.setPosition(this.props.weatherState.weather.latitude, this.props.weatherState.weather.longitude);
    // } 
    // console.log(position[0], position[1]);
    return (
    // <Map center={position} zoom={this.state.zoom} style={{height: '300px', width: '420px'}}>
    //     <TileLayer
    //       attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
    //       url='http://{s}.tile.osm.org/{z}/{x}/{y}.png'
    //     />
    //     <Marker position={position}>
    //       <Popup>
    //         <span>A pretty CSS3 popup. <br/> Easily customizable.</span>
    //       </Popup>
    //     </Marker>
    // </Map>
    <ReactMapGL
      {...this.state.viewport}
      width={600}
      height={350}
      interactive={false}
      dragPan={false}
      logoPosition="top-right"
      onViewportChange={(viewport) => console.log('viewport updated')}
      mapStyle="mapbox://styles/mapbox/streets-v11"
      mapboxApiAccessToken="pk.eyJ1IjoicmVkdmVsb2NpdHkiLCJhIjoiY2p1bDVzMmwzMXg5YjRkb2FjNXIwOWFnbCJ9.99QKd-qk4VJI4I8bj8rUWg"
    />
  )
 }
}
